        }
    }

    @else {
        .mx_#{$m} {
            margin: $m*1px;
        }
    }
}

@mixin my ($m) {
    @if ($m==a) {
        .my_#{$m} {
            margin: auto;
        }
    }

    @else {
        .my_#{$m} {
            margin: $m*1px;
        }
    }
}

/*                                               PADDING                                */
@mixin p ($m) {
