@mixin hamburguesa($bcolor, $fcolor, $bMnu, $direccion, $borderTk, $ancho, $pyItem, $pxitem, $marginItem, $fontsize) {

    .menu-wrap {
        position: relative; //cambio
        // right: 100px;
        // text-align: center;
        // margin: 0 auto;
        // width: 100%;
        z-index: 1;
        display: flex;
        flex-direction: row;
        justify-content: center;

        .toggler {
            @extend %mnu;
            z-index: 2;
            cursor: pointer;
            width: 50px;
            height: 50px;
            opacity: 0;
        }

        .toggler:checked~.menu {
            visibility: visible;

            >div {
                transform: scale(1);
                transition-duration: $tiempoMedio;

                >div {
                    opacity: 1;
                    transition: opacity $tiempoCorto ease 0.4s;
                }
            }
        }

        .toggler:checked+.hamburger>div {
            transform: rotate(135deg);

        }

        .toggler:checked+.hamburger>div:before,
        .toggler:checked+.hamburger>div:after {
            top: 0;
            transform: rotate(90deg);
        }



        .hamburger {
            //contenedor de la hamburguesa
            @extend %mnu;
            z-index: 1;
            width: 60px;
            height: 60px;
            padding: 1rem;
            background: $bcolor;
            border: $bMnu solid 2px;
            box-shadow: 3px 3px 40px $bcolor;
            border-radius: 15%;
            display: flex;
            align-items: center;
            justify-content: center;


            div {
                //linea central de la hamburguesa
                position: relative;
                flex: none;
                width: 100%;
                height: 2px;
                background: $fcolor;
                display: flex;
                align-items: center;
                justify-content: center;
                transition: all $tiempoCorto ease;
            }

            //crea las 3 lineas de la hamburguesa
            >div::after {
                content: '';
                position: absolute;
                z-index: 1;
                width: 100%;
                height: 2px;
                background: inherit;
                top: 10px;
            }

            >div::before {
                content: '';
                position: absolute;
                z-index: 1;
                top: -10px;
                width: 100%;
                height: 2px;
                background: inherit;
            }
        }


        .menu {
            position: absolute; //cambio

            @if $direccion==r {
                right: calc(100% - 60px);
            }

            @else {
                left: 100%;
            }

            border: $bcolor solid $borderTk;
            border-radius: 3%;
            box-shadow: 5px 5px 15px $bMnu;
            transform: translatey(60px);
            top: 0;
            padding: 0;
            margin: 0;

            // left: 0;
            @if $ancho==a {
                width: auto;
            }

            @else {
                width: $ancho;
            }

            height: auto; //100
            visibility: hidden;
            overflow: visible;
            display: flex;
            align-items: center;
            justify-content: center;



            >div {
                // define un fondo de pantalla para todo el menu
                background: $bMnu;
                // border-radius: 50%;
                width: 100%; ///  vh
                height: auto; // vh
                display: flex;
                flex: none;
                align-items: flex-start;
                justify-content: flex-start;
                transform: scale(0);
                transition: all $tiempoCorto ease;


                >div {
                    //CONTENEDOR DEL MENU
                    margin: 3px;
                    text-align: center;
                    max-width: 90vw;
                    max-height: 100vh;
                    opacity: 0;
                    transition: opacity $tiempoCorto ease;
                    width: 100%;


                    >ul>li {
                        //item

                        margin: $marginItem;
                        list-style: none;
                        color: setcolor($bcolor); //revisar
                        font-size: size($fontsize);

                        padding: $pyItem $pxitem;

                        >a {
                            //propiedades del link
                            color: inherit;
                            text-decoration: none;
                            transition: color $tiempoCorto ease;
                            text-align: center;
                            margin: 0 auto;

                        }
                    }
                }
            }
        }
    }
}

/*
PARAMETROS:
$bcolor Determina el color del background de la hamburgesa 
$fcolor Color de la hamburguesa
$bMnu Borde del menu y background del submenu
$direccion hacia donde sale la rama del submenu (r) para derecha otra cosa para izquierda
$borderTk Grosor del borde del menu 
