@mixin drop($name, $bcolor, $fcolor, $bMnu, $direccion, $borderTk, $ancho, $pyItem, $pxitem, $marginItem, $fontsize) {
    .drop_#{$name} {
        @include fxc(col, nowrap);
        position: relative;

        @if $direccion==l {
            >span::before {
                display: inline-block;
                content: "◄";
                transition: all $tiempoMedio ease;
                margin-right: 10px;
                font-size: size(s);
                z-index: 5;
                color: setcolor($bcolor);

            }
        }

        @else {
            @if $direccion==r {
                >span::after {
                    display: inline-block;
                    content: "►";
                    margin-left: 10px;
                    font-size: size(s);
                    z-index: 5;
                    color: setcolor($bcolor);
                }

            }

            @else {
                >span::after {
                    display: inline-block;
                    content: "►";
                    transition: all $tiempoMedio ease;

                    margin-left: 10px;
                    font-size: size(s);
                    z-index: 5;
                    color: setcolor($bcolor);
                }

                >span::before {
                    display: inline-block;
                    content: "◄";
                    transition: all $tiempoMedio ease;
                    margin-right: 10px;
                    font-size: size(s);
                    z-index: 5;
                    color: setcolor($bcolor);

                }


            }
        }

        >ul {
            border-radius: 10% 25%;
            ;
        }

        @if $direccion==l {
            .toggler_#{$name}:checked~span::before {
                transform: translateX(-20px);
                transition: all $tiempoMedio ease;

            }
        }

        @else {
            @if $direccion==r {
                .toggler_#{$name}:checked~span::after {
                    transform: translateX(20px);
                    transition: all $tiempoMedio ease;
                }
            }

            @else {
                @if $direccion==c {
                    .toggler_#{$name}:checked~span::after {
                        transform: rotate(90deg);
                    }

                    .toggler_#{$name}:checked~span::before {
                        transform: rotate(-90deg);
                    }

                }

            }
        }


    }

    .submnu_#{$name} {
        display: none;
        opacity: 0;
        background-color: $bMnu;
        border: $bcolor solid $borderTk;
        border-radius: 3%;
        overflow: hidden;
        height: calc(auto +15px);
        box-sizing: content-box;
        list-style: none;
        transition: opacity, transform 1s ease;
        margin-bottom: 15px;

        @if $ancho=a {
            width: auto;
        }

        @else {
            width: $ancho;
        }

        li {
            list-style: none;
            width: calc(90% - (#{$marginItem}*2));
            $cuenta: calc(#{$pxitem} - (#{$marginItem}*2));
            padding: $pyItem #{$cuenta};
            margin: $marginItem;
            z-index: 4;
            border-radius: 10% 25%;

        }

        li>a {
            text-decoration: none;
            width: 100%;
            margin: 0;
            height: auto;
            color: setcolor($bcolor); // revisar
            list-style: none;
            font-size: size($fontsize);
        }

    }


    .toggler_#{$name} {
        opacity: 0;
        width: 100%;
        height: 100%;
        position: absolute;
        border-radius: 10% 25%;

        left: 0;
        top: 0;
        z-index: 3;
    }

    .toggler_#{$name}:checked~ul {
        @include fxc(col, 0);
        opacity: 1;
        min-width: 200px;
        width: auto;
        height: auto;

        @if $direccion==l {
            overflow: visible;
            transition: all $tiempoMedio ease;
            position: absolute;
            top: 0;
            right: calc(#{$borderTk} + #{$marginItem} + 100%);
            transform: translateY(calc(-1 * (#{$borderTk} + #{$marginItem})));
        }

        @else {
            @if $direccion==r {
                transition: all $tiempoMedio ease;
                overflow: visible;
                position: absolute;
                top: 0;
                left: calc(#{$borderTk} + #{$marginItem} + 100%);
                transform: translateY(calc(-1 * (#{$borderTk} + #{$marginItem})));
            }

            @else {
                transition: all $tiempoMedio ease;
                position: relative;
                width: 100%;
                border-radius: 0;
            }

            transition: all $tiempoLargo ease;

            list-style: none;

        }
    }





}

